// Create a storage class to handle all localStorage functions
class Storage {
    static getTheme() {
        let theme = localStorage.getItem('theme');
        let theme_setting;
        if (theme) {
            if (theme === 'darkmode') {
                theme_setting = 'darkmode';
            } else {
                theme_setting = 'default';
            }
        } else {
            theme_setting = 'default';
        }
        return theme_setting;
    }
}

