/*** App Updates ***/
// User greeting
(function greeting() {
    var greeting, icon;
    if (hours < 12) {
        greeting = "Good morning!";
        icon = "sunrise";
    } else if (hours < 18) {
        greeting = 'Good afternoon!';
        icon = "noon";
    } else {
        greeting = "Good evening!"
        icon = "cloudy-night";
    }
    $('.user-greeting').html(greeting);
    $('.greeting-icon').attr('src', "dist/images/greeting-icons/" + icon + ".svg");
})();
/*** /.App Updates ***/

/*** Alerts ***/
// General info alert
function infoAlert(message) {
    iziToast.show({
        theme: 'dark',
        timeout: 5000,
        closeOnClick: true,
        close: false,
        progressBar: false,
        backgroundColor: 'var(--dark-blue)',
        icon: 'bi bi-info-circle text-white-inverse',
        message: message,
        messageColor: 'var(--white-inverse)',
        position: 'topCenter'
    });
}

// Sweetalert2 success
function swalSuccess(message) {
    Swal.fire(
        '',
        message,
        'success',
    );
}

// General success alert message
function successAlert(message) {
    iziToast.show({
        theme: 'dark',
        timeout: 2000,
        closeOnClick: true,
        overlay: false,
        close: false,
        backgroundColor: 'var(--success)',
        progressBar: false,
        icon: 'bi bi-check2-circle text-light',
        message: message,
        messageColor: '#fff',
        position: 'topCenter'
    });
}

// Success alert message with gift image
function giftSuccessAlert(gift_image, message) {
    iziToast.show({
        animateInside: true,
        theme: 'dark',
        timeout: 3000,
        closeOnClick: true,
        overlay: false,
        close: false,
        backgroundColor: 'var(--success)',
        progressBar: false,
        icon: 'bi bi-check2-circle text-light',
        image: '/storage/gifts/' + gift_image,
        imageWidth: 60,
        message: message,
        messageColor: '#fff',
        position: 'topCenter'
    });
}

// General warning alert
function warningAlert(message) {
    iziToast.warning({
        theme: 'dark',
        timeout: 5000,
        closeOnClick: true,
        close: false,
        progressBar: false,
        backgroundColor: 'var(--warning)',
        icon: 'bi bi-exclamation-circle text-dark',
        message: message,
        messageColor: '#000',
        position: 'topCenter'
    });
}
/*** /.Alerts ***/