// Rating category
function ratingCategory(rating) {
    var output = '';
    if (rating >= 0 && rating < 1.5) {
        output = 'Very Bad';
    } else if (rating >= 1.5 && rating < 2.5) {
        output = 'Bad';
    } else if (rating >= 2.5 && rating < 3.5) {
        output = 'Fair';
    } else if (rating >= 3.5 && rating < 4.5) {
        output = 'Good';
    } else {
        output = 'Awesome';
    }
    return output;
}

function imageZoom(imgID, resultID) {
    var img, lens, result, cx, cy;
    img = document.getElementById(imgID);
    result = document.getElementById(resultID);

    // Create zooming lens
    lens = document.createElement('div');
    lens.setAttribute('class', 'img-zoom-lens');

    // Insert lens
    img.parentElement.insertBefore(lens, img);

    // Claculate the ratio between the result-div and the lens
    cx = result.offsetWidth / lens.offsetWidth;
    cy = result.offsetHeight / lens.offsetHeight;

    // Set background properties for the result div
    result.style.backgroundImage = 'url("' + img.src + '")';
    result.style.backgroundSize = (img.width * cx) + 'px' + (img.height * cy) + 'px';

    // Execute the function when someone moves the cursor over the image, or the lens
    lens.addEventListener('mousemove', moveLens);

    // For touch screens
    img.addEventListener('touchmove', moveLens);
    lens.addEventListener('touchmove', moveLens);

    function moveLens(e) {
        var pos, x, y;

        // Prevent any other actions that may occur when moving
        // Over the image 
        e.preventDefault();

        // Get the cursor's x and y positions 
        pos = getCursorPos(e);

        // Calculate the position of the lens 
        x = pos.x - (lens.offsetWidth / 2);
        y = pos.y - (lens.offsetHeight / 2);

        // Prevent the lens from being positioned outside the image 
        if (x > img.width - lens.offsetHeight) {
            x = img.width - lens.offsetWidth;
        }

        if (x < 0) {
            x = 0;
        }

        if (y > img.height - lens.offsetHeight) {
            y = img.height - lens.offsetHeight;
        }

        if (y < 0) {
            y = 0;
        }

        // Set the position of the lens 
        result.style.backgroundPosition = '-' + (x + cx) + 'px -' + (y + cy) + 'px';
    }

    function getCursorPos(e) {
        var a, x = y = 0;
        e = e || window.event;

        // Get the x and y positions of the image
        a = img.getBoundingClientReact();

        // Calculate the cursor's x and y coordinates relative to the image 
        x = e.pageX - a.left;
        y = e.pageY - a.top;

        // Consider any page scrolling 
        x = x - window.pageXOffset;
        y = y - window.pageYOffset;
        return {
            x: x,
            y: y
        };
    }
}
